import axios from "axios";
import { useState } from "react";

function KakaoPlace(props){
    let [obj, setObj] = useState([]);

    let PKobj = ['']
    let FDobj = [''];
    let CEobj = [''];


    /* kakaoAPI */
    const placeResult = (code) => {

       if(code === 'PK6'){
            if(PKobj.length === 1){ //empty
                getPlace(code);
            }else { 
                setObj(PKobj)
            }
       }else if(code === 'FD6'){
        console.log("fdobj : " + FDobj)
            if(FDobj.length === 1){
                console.log("111")
                getPlace(code);
            }else { 
                console.log("222 : " + FDobj.length)
                setObj(FDobj)
            }
       }else{
            if(CEobj.length === 1){
                getPlace(code);
            }else { 
                setObj(CEobj)
            }
       } 
    }

    const returnPlace = obj.map((e, i) => {
            console.log("returnplace obj ;" + JSON.stringify(e))
            return(
               <div key={i} style={{textAlign:"left"}}>
                  <span>place name : <a href={e.place_url} target="_blank">{e.place_name}</a></span><br/>
                  <span>address name : {e.address_name}</span><br/>
                  <span>phone : {e.phone}</span><br/>
                  <hr/>
                </div>
            )
     })




    const getPlace = (code) => {
        const apiKey = process.env.REACT_APP_KAKAO_KEY
        /*
            x = longitude
            y = latitude
            category_group_code = PK6(carpark), FD6(restaurant), CE7(cafe)
        */

        axios.get(`https://dapi.kakao.com/v2/local/search/category.json?category_group_code=${code}&x=${props.lng}&y=${props.lat}&radius=1000&size=5`, {
            headers : {"Authorization" : "KakaoAK "+ apiKey}
        }).then( (res) => {
             console.log("res.data ::" + JSON.stringify(res.data.documents));
             if(code === 'PK6'){
                PKobj.push(res.data.documents);
                setObj(PKobj)
             }else if(code === 'FD6'){
                console.log("???")
                FDobj.push(res.data.documents);
                console.log("???FDobj :: " + FDobj)
                setObj(FDobj)
             }else{
                CEobj.push(res.data.documents);
                setObj(CEobj);
             }
        })
    }


    return(
        <div>
             <button onClick={() => {placeResult('FD6')}}>Restaurant</button>
             <button onClick={() => {placeResult('CE7')}}>Cafe</button>
             <button onClick={() => {placeResult('PK6')}}>Carpark</button>
             <br/>

            {returnPlace}
        </div>
    )
}


export default KakaoPlace;
