import axios from "axios";

function KakaoPlace(props){

    let PKobj = ''
    let FDobj = '';
    let CEobj = '';


    /* kakaoAPI */
    const placeResult = (code) => {

       if(code === 'PK6'){
            if(PKobj === '' || PKobj === null){
                console.log("here : PK6")
                getPlace(code);
            }else { returnPlace(PKobj)}
       }else if(code === 'FD6'){
            if(FDobj === '' || FDobj === null){
                console.log("here : FD6")
                getPlace(code);
            }else { returnPlace(FDobj)}
       }else{
            if(CEobj === '' || CEobj === null){
                console.log("here : CE")
                getPlace(code);
            }else { returnPlace(CEobj)}
       } 
    }

    const returnPlace = (obj) => {

        return(
            <>  
                {
                    obj.map((e, i) => {
                        <>
                        <span>{e.placeName}</span>
                        <span>{e.place_url}</span>
                        <span>{e.address_name}</span>
                        <span>{e.phone}</span>

                        </>
                    })
                }
            </>
        )
    }

    const getPlace = (code) => {
        const apiKey = process.env.REACT_APP_KAKAO_KEY
        /*
            x = longitude
            y = latitude
            category_group_code = PK6(carpark), FD6(restaurant), CE7(cafe)
        */

        axios.get(`https://dapi.kakao.com/v2/local/search/category.json?category_group_code=${code}&x=${props.lng}&y=${props.lat}&radius=1000&size=5`, {
            headers : {"Authorization" : "KakaoAK "+ apiKey}
        }).then( (res) => {
             console.log("res.data ::" + JSON.stringify(res.data.documents));
             if(code === 'PK6'){
                PKobj = res.data
                returnPlace(PKobj)
             }else if(code === 'FD6'){
                FDobj = res.data
                returnPlace(FDobj)
             }else{
                CEobj = res.data
                returnPlace(CEobj)
             }
        })
    }


    return(
        <div>
             <button onClick={() => {placeResult('FD6')}}>Restaurant</button>
             <button onClick={() => {placeResult('CE7')}}>Cafe</button>
             <button onClick={() => {placeResult('PK6')}}>Carpark</button>

             {placeResult}
        </div>
    )
}
export default KakaoPlace;
