import {useState} from "react";


const ReviewList = () => {
    const [files, setFiles] = useState([]);
    const [currentSlide, setCurrentSlide] = useState(0);

    const handleNextSlide = () => {
        setCurrentSlide((currentSlide + 1) % files.length);
    };

    const handlePrevSlide = () => {
        setCurrentSlide((currentSlide - 1 + files.length) % files.length);
    };

    // const handleFileChange = (e) => {
    //     const newFiles = [...e.target.files];
    //     setFiles(newFiles);
    //     setCurrentSlide(0);
    // };
/*
    const filePreviewElements = files.map((file, index) => (
        <div
            className={`slide ${index === currentSlide ? 'active' : ''}`}
            key={file.name}
        >
            {index === currentSlide && (
                <>
                    <img src={URL.createObjectURL(file)} alt={file.name} />
                    <p className="slide-caption">{file.name}</p>
                </>
            )}
        </div>
    ));
*/

    const filePreviewElements = files.map((file, index) => (
        <div>
             <img src={URL.createObjectURL(file)} alt={file.name} />
             <p className="slide-caption">{file.name}</p>
        </div>
    ));

    const handleFileChange = (e) => {
        console.log("e : " + e.target.files)
        const fileArr = e.target.files;
        let fileURLs = [];
        let file;
        let filesLength = fileArr.length > 10 ? 10 : fileArr.length;

        for (let i = 0; i < filesLength; i++) {
            file = fileArr[i];

            let reader = new FileReader();
            reader.onload = () => {
              console.log("?? " + reader.result);
              fileURLs[i] = reader.result;
              setFiles([...fileURLs]);
            };
            reader.readAsDataURL(file);
          }
    }

    return(
        <>
        <div style={{marginLeft:"5%", marginRight:"5%"}}>
            <hr/>
            <h1>Review</h1>
            <button className="reviewFilterBtn">Rating▼</button>
            <button className="reviewFilterBtn">Recent▼</button>

            <section style={{paddingTop:"50px"}}>
                <h2>4.5 ⭐️ ⭐️ ⭐️ ⭐️ ⭐️   / 298 Reviews</h2> <br/>
            </section>
            <section style={{textAlign:"right"}}>
                <button>Write Review</button>
            </section>

            {/* Write Review */}
            <section className="writeReview" style={{textAlign:"left"}}>
                <div style={{display:"flex"}}>
                    <h2>Rating</h2>
                    <span>⭐️ ⭐️ ⭐️ ⭐️ </span>
                </div>
                <h2>How was your experience?</h2>
                <textarea style={{height:"200px", padding:"2%", border:"solid"}}/>
                <h2>Add Photo</h2>
                <p>
                    <input type="file" 
                           id="file"
                           accept="image/jpg,image/png,image/jpeg,image/gif"  
                           onChange={handleFileChange} 
                           multiple/>
                    <div className="slideshow-container">
                        {filePreviewElements}
                        <button className="prev-button" onClick={handlePrevSlide}> &#10094; </button>
                        <button className="next-button" onClick={handleNextSlide}>  &#10095;</button>
                    </div>
                </p>

                <button>submit</button>
                <hr/>
            </section>


            <section>
                <span><b>Abc1233</b> ⭐️ ⭐️ ⭐️ ⭐️ ⭐️ &nbsp; |&nbsp; 2021/02/17</span><br/>
                <span><b>PostTitle</b></span><br/>
                <span>reviewreviewreviewreviewreviewreviewreviewreviewreview</span><br/>
                <div>img imgimg</div>
                <hr/>

                <span><b>Abc1233</b> ⭐️ ⭐️ ⭐️ ⭐️ ⭐️ &nbsp; |&nbsp; 2021/02/17</span><br/>
                <span><b>PostTitle</b></span><br/>
                <span>reviewreviewreviewreviewreviewreviewreviewreviewreview</span><br/>
                <div>img imgimg</div>
                <hr/>
            </section>



        </div>
        </>
    )
}

export default ReviewList;
