import { useEffect, useState } from "react";
import restaurant  from '../assets/img/restaurant.png';

const { kakao } = window;

const KakaoMap = (props) => {
    console.log("props: " +props.lat + " / " +  props.lng  + " / " + JSON.stringify(props.markers))

    useEffect(()=>{
        const container = document.getElementById('map');
        const options = { 
            center: new kakao.maps.LatLng(props.lat, props.lng),
            level : 5
        };
        const map = new kakao.maps.Map(container, options);
        const markerPosition = options.center;
        let marker = new kakao.maps.Marker({
            position: markerPosition
        });

        marker.setMap(map);
        menuMarkers();

    },[])

    const menuMarkers = () => {
        const container = document.getElementById('map');
        const options = { 
            center: new kakao.maps.LatLng(props.lat, props.lng),
            level : 5
        };
        const map = new kakao.maps.Map(container, options);
        let menuMarkersOptions = {
            imageSrc : restaurant,
            imageSize : new kakao.maps.Size(64, 69),
            imageOption : {offset: new kakao.maps.Point(27, 69)}
        }


        props.markers.forEach((e) => {
            console.log("menumarkers e : " + JSON.stringify(e))
            new kakao.maps.Marker({
                map: map,
                position: new kakao.maps.LatLng(e.lat, e.lng),
                title : e.title,
                image : menuMarkersOptions.imageSrc
            })
        });
    }


        return (
            <div>
                <div id="map" style={{width:"100%", height:"700px"}}></div> 
            </div>
        )
}

export default KakaoMap;
